import { APP_INITIALIZER, LOCALE_ID, NgModule } from '@angular/core';
import { BrowserModule, Title } from '@angular/platform-browser';

import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { I18NEXT_SERVICE, I18NextModule, I18NextTitle, ITranslationService, defaultInterpolationFormat } from 'angular-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';
import ressourcesToBackend from "i18next-resources-to-backend";
import HttpBackend from "i18next-http-backend";
import ChainedBackend from "i18next-chained-backend";

export function appInit(i18next: ITranslationService) {
  return () => i18next
      .use<any>(LanguageDetector)
      .use(ChainedBackend)
      .use(HttpBackend)
      .init({
      supportedLngs: ['en', 'de', 'fr'],
      fallbackLng: 'en',
      debug: true,
      returnEmptyString: false,
      saveMissing: true,
      ns: [
        'translations',
      ],
      interpolation: {
        format: I18NextModule.interpolationFormat(defaultInterpolationFormat)
      },
      backend: {
        backends: [
          HttpBackend,
          ressourcesToBackend((lng:string, ns:string) => import(`../assets/locales/${lng}.${ns}.json`))
        ],
        backendOptions: [{
          loadPath: '../assets/locales/{{lng}}.{{ns}}.json',
        }]
        // loadPath: '../assets/locales/{{lng}}.{{ns}}.json',
      },
    });
}

export function localeIdFactory(i18next: ITranslationService)  {
  return i18next.language;
}

export const I18N_PROVIDERS = [
{
  provide: APP_INITIALIZER,
  useFactory: appInit,
  deps: [I18NEXT_SERVICE],
  multi: true
},
{
  provide: LOCALE_ID,
  deps: [I18NEXT_SERVICE],
  useFactory: localeIdFactory
}];

@NgModule({
  declarations: [
    AppComponent
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    BrowserAnimationsModule,
    I18NextModule.forRoot()
  ],
  providers: [I18N_PROVIDERS],
  bootstrap: [AppComponent]
})
export class AppModule { }
